create table Museum
(
    mus_id      int              not null,
    mus_name    varchar(255)     not null,
    mus_picture varchar(255)     null,
    mus_grade   double default 5 null,
    mus_time    varchar(255)     null,
    mus_address text             null,
    mus_remark  text             null,
    mus_phone   varchar(12)      null,
    mus_master  varchar(255)     null,
    constraint Museum_mid_uindex
        unique (mus_id)
)
    comment '博物馆表';

alter table Museum
    add primary key (mus_id);

